import{_ as o}from"./WorkfieldsPageComponent.5066552a.js";import{b as n,F as s,r as l,o as t,e as d,f as c}from"./entry.18959fe1.js";import"./ContactAlgemeen.d9723f99.js";import"./ContactBlock.7ca620e3.js";import"./nuxt-link.d392a769.js";const j={__name:"hr",setup(m){const i=[{title:"Ben jij op zoek naar",subTitle:"DE BESTE HR KANDIDATEN?",content:"Het aantrekken van de juiste HR-professionals vraagt om een mix van inzicht, empathie en strategie. HR neemt een sleutelpositie in bij de begeleiding van het personeel, daarom gaan we actief op zoek naar de professionals die hier het beste invulling aan kunnen geven. <br> <br> HR is niet enkel gericht op werving en selectie, het bestrijkt ook aspecten zoals talentontwikkeling, compensatiebeheer, werknemerstevredenheid en conflictbemiddeling. Wij sourcen kandidaten die ervaring hebben met dit brede spectrum aan verantwoordelijkheden, met oog voor zowel de technische aspecten van HR als de menselijke dynamiek die de kern van het veld vormt. <br> <br> Een succesvolle HR-professional combineert kennis van wetten en best practices in HR met luistervaardigheden, begrip en strategisch denken. Zij dienen een verbindende rol op zich te nemen tussen management en werknemers, en een bedrijfscultuur uit te dragen die de waarden en doelen van de organisatie reflecteert. <br> <br> In onze zoektocht naar HR-professionals gaan we verder dan alleen diploma's en ervaring. We letten op de persoonlijkheid, houding en culturele fit van de kandidaat. Wij zoeken iemand met de juiste balans tussen empathie en zakelijk inzicht, die een waardevolle toevoeging zal zijn aan jouw organisatie."}];return(p,u)=>{const a=o;return t(),n("div",null,[(t(),n(s,null,l(i,(e,r)=>d("div",{class:"card",key:r},[c(a,{Title:e.title,SubTitle:e.subTitle,Content:e.content},null,8,["Title","SubTitle","Content"])])),64))])}}};export{j as default};
